  gp_Ax2d Axes;

  //Focus
  gp_Pnt2d FocusPoint;

  //Focal length
  Standard_Real FocalLength;

  //Coordinates of the vertex
  Standard_Real VertX, VertY;

  //Parameter
  Standard_Real Parameter;

  //Coefficients
  Standard_Real Coeffs[6];
